mapper.select:
select bcp.cha_ip,bcp.cha_por,bcp.cha_num,bcp.man_id,bcp.chp_com_equ,bcp.cha_pil_sta,bcp.id chaId, (select bm.man_nam from bas_man bm where bm.id = bcp.man_id) man_nam from bas_cha_pil bcp where 1 = 1
AND bcp.id = #{pileId}
AND bcp.cha_num = #{chaNum}

mapper.insert:
INSERT INTO dat_cha_inf ( cha_pil_id, cha_gun_typ, cha_gun, cha_car, sta_tim, end_tim, tim_len, sta_soc, end_soc, cha_fin_rea, cha_ele_qua, sta_rea, end_rea, cha_mon, sta_bal, cha_str, cha_str_par, car_vin,
sta_mod ) VALUES ( #{pileCode}, #{gunType}, #{gun}, #{cardID}, #{startTime}, #{endTime}, #{durationTime}, #{startSOC}, #{endSOC}, #{stopReason}, #{chargeEle}, #{chargeStart}, #{chargeEnd}, #{chargeMoney}, #{cardBalance}, #{chargeType}, #{chargeTypeParam}, #{carVIN},
#{chargeStartType} );

mapper.update:
UPDATE bas_cha_pil
    SET
    cha_pil_sta = #{chaPilSta},
    fau_sta = #{fauSta}
    WHERE 1 = 1


       AND id = #{id}


        AND chp_nam = #{chp_nam}


        AND cha_num = #{cha_num}


        AND cha_ip = #{cha_ip}

UPDATE bas_cha_pil
    SET
    cha_pil_sta = #{chaPilSta}
    WHERE 1 = 1
        AND cha_num = #{cha_num}

UPDATE use_wx
    SET
    acc_bal = #{accBal},
    use_experience = #{useExperience}
    WHERE 1 = 1
        AND openid = #{openId}
